-- USERS TABLECREATE TABLE users (user_id SERIAL PRIMARY KEY,full_name VARCHAR(100) NOT NULL,email VARCHAR(100) UNIQUE NOT NULL,password_hash VARCHAR(100) NOT NULL,role VARCHAR(20) NOT NULL);-- SERVICES TABLECREATE TABLE services (service_id SERIAL PRIMARY KEY,name VARCHAR(100) NOT NULL,description TEXT,base_price DECIMAL(10,2) NOT NULL);-- BOOKINGS TABLECREATE TABLE bookings (booking_id SERIAL PRIMARY KEY,client_id INT REFERENCES users(user_id),advisor_id INT REFERENCES users(user_id),service_id INT REFERENCES services(service_id),booking_time TIMESTAMP NOT NULL,status VARCHAR(20) DEFAULT 'Pending');-- PAYMENTS TABLECREATE TABLE payments (payment_id SERIAL PRIMARY KEY,booking_id INT REFERENCES bookings(booking_id),amount DECIMAL(10,2) NOT NULL,method VARCHAR(50),payment_time TIMESTAMP DEFAULT CURRENT_TIMESTAMP);